def login_to_flask(username, password, role):
    # Define the Flask API endpoint for login validation
    flask_login_endpoint = f"http://localhost:5000/login/{role}"

    # Send a POST request with the login credentials to Flask
    payload = {"username": username, "password": password}
    response = requests.post(flask_login_endpoint, data=payload)

    return response.ok

